import React ,{useState }from "react";
import { useSelector, useDispatch } from 'react-redux';
import { fetchAsync, clear, postReport } from "../stores/hourReport";
//import { fetchAsync, clear } from "../stores/gif";
import {Row,Col,Image,Card,Button,Form} from "react-bootstrap";
import {ResponsiveContainer,LineChart,XAxis,YAxis,CartesianGrid,Line} from "recharts";
import {makeReport} from "../stores/report";

import {SlackForm} from "./SlackForm";

function UserList() {
    const text = useSelector(state => state.hourReport.text);
    const loading = useSelector(state => state.hourReport.loading);
    const error = useSelector(state => state.hourReport.error);


    const [values, setValues] = useState({
      text : ""
    });

    return (
    <div>
      <h1 className="title">HRLog</h1>
      <ReportHandler {...{ text, loading,error }} />
    </div>
	    );


function handleInputChange(e) {
  const target = e.target;
  const value = target.type === "checkbox" ? target.checked : target.value;
  const name = target.name;
  setValues({ ...values, [name]: value });
  console.log(value)
}

}

function handleSubmit(event) {
  console.log(event.target.elements.formBasicPassword.value)
}

function UserInfoList({text}){
    const dispatch = useDispatch();
    return (
        <div>
        <ul>
            {
                text.map(
                    item => (
                        <li key={item.user_id}>{
                            <a style={{ cursor: 'pointer' }} onClick={() => dispatch(makeReport(item.user_id))}>
                            <Card>
                            　<Card.Body>
                                <Card.Title>{item.user_name}</Card.Title>
                                　<Row>
                                    <Col xs={3} md={2}>
                                    　<Image src={item.image_url}/>  
                                　　</Col>
                                　　<Col xs={12} md={8}>
                                        <Card.Text>
                                            今週の会話履歴
                                        </Card.Text>
                                        <WeekSummary {...{item}} />
                                　　</Col>
                                　</Row>
                                </Card.Body>
                            </Card>
                            </a>
                            }
                        </li>
                    )
                )
            }
        </ul>
        </div>
    )

}


function WeekSummary({item}){
    const data = [
        {name: '7/5', value: 12},
        {name: '7/6', value: 24},
        {name: '7/7', value: 6},
        {name: '7/8', value: 8},
        {name: '7/9', value: 24},
        {name: '7/10', value: 12},
        {name: '7/11', value: 6},
    ]
    return(
    <div style={{ width: '100%', height: '100px' }}>
    <ResponsiveContainer>
    <LineChart data={data}>
        <XAxis dataKey="name"/>
        <YAxis/>
        <CartesianGrid stroke="#eee" strokeDasharray="5 5"/>
        <Line type="monotone" dataKey="value" stroke="#8884d8" />
      </LineChart>
      </ResponsiveContainer>
      </div>
    )
}

function SiteSelectedCallback(cursor,e){
    console.log(`click`)
    console.log(e)
    console.log(cursor)
}




function ReportHandler({text,loading,error}){
  const dispatch = useDispatch();

    const [values, setValues] = useState({
      text : ""
    });
  console.log(text)
  if (error) {
    return <p className="notification is-danger">Error!!</p>;
  }
  if (loading) {
    return <p className="notification is-info">Loading...</p>;
      }
  if (!text) {
    console.log("success")
    return (
        <SlackForm {...{ text, loading,error }}/>
    );
  }

  return (
      <div>
        <UserInfoList {...{text}} />
    </div>
  );

  function handleInputChange(e) {
    const target = e.target;
    const value = target.type === "checkbox" ? target.checked : target.value;
    const name = target.name;
    setValues({ ...values, [name]: value });
    console.log(value)
  }

}

export default UserList;
